    <div className="table-row" key={props.i}>
      <p className="row-material">{props.matName}</p>
      <span className="row-uom">{props.uom}</span>
      <input
        className={rowClass}
        htmlFor={props.for}
        id={props.id}
        type={props.type}
        placeholder={props.placeholder}
      ></input>
    </div>
  );
}
