import { useState, useEffect } from "react";
import TableRowStatic from "../components/table-row-static";
import TableHeader from "../components/table-header";
import TableRow from "../components/table-row";
import axios from "axios";
export default function ComponentTest() {
  const [materialList, setMaterialList] = useState([]);
  let td = [];
  let fetchMaterials = () => {
    let supplierName = "VERSATILE OPERATION";
    let poStore = "LAS_928;LAS_943";
    axios
      .post("/bill_receive/material_list", {
        supplierName: supplierName,
        store_po: poStore,
      })
      .then((response) => {
        //console.log(response.data[0]);
        setMaterialList(response.data);
        console.log(response.data.length);
        response.data.forEach((m, i) => {
          let { NOMEN1 } = response.data[i];
          let { UNIT_NM } = response.data[i];
          let { BAL_QTY } = response.data[i];
          //console.log(NOMEN1);
          //console.log(UNIT_NM);
          //console.log(BAL_QTY);
        });
      });
  };
  useEffect(() => {
    materialList.forEach((m, i) => {
      //console.log(m[i].NOMEN1);
      //console.log(m[i].UNIT_NM);
      //console.log(m[i].BAL_QTY);
      td.push(
        <TableRow
          matName={m[0].NOMEN1}
          uom={m[0].UNIT_NM}
          type="number"
          placeholder={m[0].BAL_QTY}
          key={i}
        />
      );
    });
  }, [materialList]);
  return (
    <>
      <div className="typematerialList">
        <button onClick={fetchMaterials} />
        <TableHeader
          heads={["Material", "UOM", "Qty"]}
          flexG={[{ flexGrow: 0.4 }, { flexGrow: 0 }, { flexGrow: 0 }]}
        />
        <div className="material-rows">
          {
            materialList.map((mat, idx) => {
              console.log(mat);
              //console.log(idx);
              <TableRow matName={mat} uom={mat} qty={mat} key={idx} />;
            })
            //<TableRowStatic matName={m.mat} uom={m.uom} qty={m.qty} />
          }
        </div>
      </div>
    </>
  );
}
