import axios from "axios";
import React, { useState, useEffect } from "react";
import Button from "../components/button";
import Input from "../components/input";
import InputBoxValue from "../components/input-box-value";
import SelectInput from "../components/input-select";
import TableHeader from "../components/table-header";
import TableRowStatic from "../components/table-row-static";
const ipL = "http://192.168.1.105:3500";
export default function CourierIn() {
  const [addedMat, setAddMat] = useState([]);
  const [rowData, setRowData] = useState([]);
  const [tempArr, setTempArr] = useState([1, "a", 50]);

  useEffect(() => {
    axios.get(`${ipL}/courier/client_list`).then((response) => {
      console.log(response.data);
    });
  }, []);
  let handleClick = () => {
    let old = [...addedMat];
    let cur = [...addedMat];
    //console.log(addedMat);
    let nmat = document.getElementById("materialNameInput").value;
    let nuom = document.getElementById("materialUOMInput").value;
    let nqty = document.getElementById("materialQtyInput").value;
    let str = { mat: nmat, uom: nuom, qty: nqty };
    //cur.push(str);
    //console.log(cur);
    cur.push(str);
    setAddMat(cur);
    clearForm();
    //console.log(cur);
    //console.log(addedMat);
    addedMat.map((mat, idx) => {
      //console.log(addedMat[idx]);
    });
    //return cur;
  };

  let data = () => {
    let matData;
    if (addedMat.length === 0) {
      return [];
    }
    for (let idx = 0; idx <= addedMat.length; idx++) {
      matData.push(
        <TableRowStatic
          matName={addedMat[idx].mat}
          uom={addedMat[idx].uom}
          qty={addedMat[idx].qty}
          id={idx}
        />
      );
      //console.log(addedMat[idx]);
    }
    return matData;
  };

  function clearForm() {
    document.getElementById("materialNameInput").value = "";
    document.getElementById("materialUOMInput").value = "";
    document.getElementById("materialQtyInput").value = "";
  }

  return (
    <div className="billScreen">
      <div>
        <div className="initEntry">
          <div className="screen-header">Courier In</div>
          <div>
            <div className="form-group-1">
              <Input
                name="Date"
                for="date"
                type="date"
                placeholder="dd/mm/yyyy"
              />
              <Input
                name="Received By"
                for="text"
                type="text"
                placeholder="Name"
                id="recvBy"
              />
              <SelectInput
                id="supName"
                list={genSenderList()}
                name="Sender Name"
              />
              <InputBoxValue
                id="newSenderInput"
                for="text"
                type="text"
                placeholder="New Sender"
              />
            </div>

            <div className="form-group-2">
              <Input
                id="awbInput"
                for="text"
                type="text"
                placeholder="AWB Number"
                name="AWB"
              />
              <Input
                id="courierPartner"
                for="text"
                type="text"
                placeholder="Courier Partner"
                name="Courier Service"
              />
            </div>
          </div>
          <div className="form-group-3"></div>
        </div>
        <div className="typematerialList">
          <TableHeader
            heads={["Material", "UOM", "Qty"]}
            flexG={[{ flexGrow: 0.5 }, { flexGrow: 0 }, { flexGrow: 0 }]}
          />
          <div className="material-rows-single">
            <div className="materialRow">
              <InputBoxValue
                id="materialNameInput"
                for="text"
                type="text"
                placeholder="Enter Material Name"
              />
              <InputBoxValue
                id="materialUOMInput"
                for="text"
                type="text"
                placeholder="Enter UOM"
              />
              <InputBoxValue
                id="materialQtyInput"
                for="number"
                type="number"
                placeholder="Enter Qty"
              />
            </div>
          </div>
        </div>
        <Button
          type="button"
          id="poAddButton"
          value="Add"
          onClickFunc={handleClick}
        />
        <Button
          type="button"
          id="uploadImage"
          value="Upload Image"
          onClickFunc={uploadImage}
        />
      </div>
      <div className="compEntry">
        <div className="typematerialList">
          <TableHeader
            heads={["Material", "UOM", "Qty"]}
            flexG={[{ flexGrow: 0.4 }, { flexGrow: 0 }, { flexGrow: 0 }]}
          />
          <div className="material-rows">
            {addedMat.map((mat) => {
              return (
                <TableRowStatic
                  matName={mat.mat}
                  uom={mat.uom}
                  qty={mat.qty}
                  id={mat}
                />
              );
            })}
          </div>
        </div>

        <Button
          type="button"
          id="submitBill"
          value="Submit Bill"
          onClickFunc={submitBill}
        />
      </div>
    </div>
  );
}

function submitBill() {
  alert("Button CLicked");
}

function uploadImage() {}

let genSenderList = () => {
  let arr = ["Create New"];
  axios.get(`${ipL}/courier/client_list`).then((response) => {
    console.log(response.data.map);
  });
  return arr;
};
